//yp=cos(tm)*0.4 ;

xp=xp*sin(tm*3);
yp=yp*cos(tm*2.6);

xp=0+xp*sin(tm*0.43);
yp=yp*(cos(tm*1.79)*0.5+.5);

xp=xp+wavex;
yp=yp+wavey;

x=xp+.25;
y=yp+.5;


trailsize=0.85;
head=above(a,trailsize);
tail=1-head;
r=1*head + 0.5*tail;
g=0.8*head + 0.3*tail;
b=0.5*head;

a=a*0.25

