waves.point_eqs_eel:
x=sample;

y1=sin(sample*6.28*q2)*.5;
y2=cos(sample*6.28*q4)*.5;
y3=tan(sample*6.28*q6)*.1+.5;
y=y1*q1+y2*q3+y3*q5+value1*.05-value2*.05;


r1=y*value1;
g1=x*value2;
b1=1-r-g;

r2=sin(value1*6.28)*.4+.4;
g2=sin(value2*6.28)*.4+.4;
b2=cos(value1*3.14+value2*3.14)*.4+.4;

r3=value2;
g3=value1;
b3=value1*value2*q2;


r=r1*q1+r2*q3+r3*q5;
g=g1*q1+g2*q3+g3*q5;
b=b1*q1+b2*q3+b3*q5;

init_eqs_eel:
beatrate=5;
avgbeatrate=1;
bbtime=1;
lbbtime=.5;
avgdb=.5;
vol=1;
avgvol=.2;
mtime=1000;

tt=1000*treb;
bt=1000*bass;
mt=1000*mid;

frame_eqs_eel:
db=(bass-lbass)*fps;
lbass=bass;
avgdb=avgdb*.99+abs(db)*.01;

//beatdection
rawbeatb=above(db,avgdb*2);
beatb=rawbeatb*above(time-lbbtime,.75*avgbeatrate)*above(bass_att,.3);

//beatrate calc
bbtime=time*beatb;
beatrate=beatb*(bbtime-lbbtime)+(1-beatb)*beatrate;
avgbeatrate=beatb*(avgbeatrate*.999+beatrate*.001)+(1-beatb)*avgbeatrate;
lbbtime=time*beatb+(1-beatb)*lbbtime;

bc=bc+beatb;



vol=(rawbeatb*(db-(avgdb))*.01);
avgvol=avgvol*.99+vol*.05;

mtime=mtime+min(avgvol*.5,.25);

//decay=.99;
warp=0;
wrap=1;


warp = 0;

tic = min(time - tin,.1);
tin = time;

ra = 10;
treb_avg = tic*(treb_avg*(1/tic - ra) + ra*treb);
mid_avg = tic*(mid_avg*(1/tic - ra) + ra*mid);
bass_avg = tic*(bass_avg*(1/tic - ra) + ra*bass);

rb = 1;
vav = tic*(vav*(1/tic - rb) + rb*(bass+treb+mid)*.33333);

tt = tt + tic*treb_avg;
mt = mt + tic*mid_avg;
bt = bt + tic*bass_avg;
vt = vt + tic*(treb_avg+mid_avg+bass_avg)*.33333;

sz = .2;

q1 = .5 + sz*sin(tt*.629) - sz*sin(tt*.107);
q2 = .5 + sz*sin(tt*.987) - sz*sin(tt*.456);

q3 = .5 + sz*sin(mt*.654) - sz*sin(mt*.355);
q4 = .5 + sz*sin(mt*.548) - sz*sin(mt*.875);

q5 = .5 + sz*sin(bt*.687) - sz*sin(bt*.318);
q6 = .5 + sz*sin(bt*.465) - sz*sin(bt*.526);


q7=mtime;
q8=dbass;

pixel_eqs_eel:
xd = q1-x;
yd = q2-y;

dis = pow(xd*xd+yd*yd,.5);
des = 1 - dis*.7071068;
des = pow(des,10*q8)*-treb;
an = acos(abs(xd)/dis);

dx = sign(xd)*.01*cos(an)*des;
dy = sign(yd)*.01*sin(an)*des;

//dy = dy + sign(xd)*treb*.05*cos(an)*des;
//dx = dx + sign(yd)*treb*.05*sin(an)*des*-1;


dx=dx-sign(yd)*sin(q7+an)*.001*treb_att*des;
dy=dy+sign(xd)*cos(q7+an)*.001*treb*des;

sy=1+dx*des;
sx=1-dy*des;


xd = q3-x;
yd = q4-y;

dis = pow(xd*xd+yd*yd,.5);
des = 1 - dis*.7071068;
des = pow(des,10*q8)*-mid;
an = acos(abs(xd)/dis);

dx = dx + sign(xd)*.01*cos(an)*des;
dy = dy + sign(yd)*.01*sin(an)*des;

//dy = dy + sign(xd)*mid*.05*cos(an)*des;
//dx = dx + sign(yd)*mid*.05*sin(an)*des*-1;


dx=dx-sign(yd)*sin(q7+an)*.001*mid_att*des;
dy=dy+sign(xd)*cos(q7+an)*.001*mid*des;

sy=1+dx*des;
sx=1-dy*des;

xd = q5-x;
yd = q6-y;

dis = pow(xd*xd+yd*yd,.5);
des = 1 - dis*.7071068;
des = pow(des,10*q8)*-bass;
an = acos(abs(xd)/dis);

dx = dx + sign(xd)*.01*cos(an)*des;
dy = dy + sign(yd)*.01*sin(an)*des;

//dy = dy + sign(xd)*bass*.05*cos(an)*des;
//dx = dx + sign(yd)*bass*.05*sin(an)*des*-1;


dx=dx-sign(yd)*sin(q7+an)*.001*bass_att*des;
dy=dy+sign(xd)*cos(q7+an)*.001*bass*des;

sy=1+dx*des;
sx=1-dy*des;

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = ((texture (sampler_blur1, uv).xyz * scale1) + bias1);
 ret_1 = (ret_1 + ((texture (sampler_blur1, 
   (uv + ret_1.xy)
 ).xyz * scale1) + bias1));
 vec2 tmpvar_2;
 tmpvar_2.x = (uv.x - ret_1.y);
 tmpvar_2.y = (uv.y - ret_1.y);
 ret_1 = (ret_1 + ((texture (sampler_blur1, tmpvar_2).xyz * scale1) + bias1));
 vec3 tmpvar_3;
 tmpvar_3 = pow (((
   sqrt(dot (ret_1, ret_1))
  * ret_1) * 1.33), (0.89 - (
   (texture (sampler_blur3, uv).xyz * scale3)
  + bias3)));
 ret_1 = (tmpvar_3 * tmpvar_3.z);
 ret_1 = (ret_1 - texture (sampler_main, uv).xyz);
 ret_1 = (ret_1 / (texture (sampler_noisevol_hq, (
   ((vec3(0.05, 0.05, 0.0) * uv.xyy) * (texsize.xyy * texsize_noisevol_hq.zww))
  + 
   (vec3(0.0, 0.0, 0.4) * time)
 )) * 6.5).xyz);
 ret_1 = (1.0 - ret_1);
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ret_1;
 ret = tmpvar_4.xyz;
}

