waves.point_eqs_eel:
//plot x,y,z to point on circle
smp=sample*6.283;
xp=sin(smp )*0.20;
yp=cos(smp )*0.20;
zp=0;


//alter shape;
angy=sin(sample*6.28*4 +t1 )*6.28;
xq=xp*cos(angy) - zp*sin(angy);
zq=xp*sin(angy) + zp*cos(angy);
xp=xq;
zp=zq;


//rotate on y axis;
angy=t1*0.1;
xq=xp*cos(angy) - zp*sin(angy);
zq=xp*sin(angy) + zp*cos(angy);
xp=xq;
zp=zq;

//rotate on x axis
axs1 = sin(t1*0.15) + 1.6;
yq= yp*cos(axs1) - zp*sin(axs1);
zq= yp*sin(axs1) + zp*cos(axs1);
yp=yq;
zp=zq;

//rotate on y axis again
axs2 = sin(t1*0.1)*3.3;
xq=xp*cos(axs2) - zp*sin(axs2);
zq=xp*sin(axs2) + zp*cos(axs2);
xp=xq;
zp=zq;

//stretch y axis to compensate for aspect ratio
yp=yp*1.2;

//push forward into viewpace
zp=zp+2.1;

//project x,y,z into screenspace
xs=xp/zp;
ys=yp/zp;

//center 0,0 in middle of screen
x=xs+0.5+q4;
y=ys+0.5+q5;

r=1-q1;
g=1-q2;
b=1-q3;

frame_eqs_eel:
wave_r = 0.85 + 0.35*sin(0.437*time+1) + 0.55*sin(0.197*time+4);
wave_g = 0.85 + 0.35*sin(0.544*time+2) + 0.55*sin(0.143*time+5);
wave_b = 0.85 + 0.35*sin(0.751*time+3) + 0.55*sin(0.117*time+6);
wave_r = sqrt(wave_r);
wave_g = sqrt(wave_g);
wave_b = sqrt(wave_b);
rot = rot + 0.010*( 0.60*sin(0.381*time) + 0.40*sin(0.579*time) );
cx = cx + 0.210*( 0.60*sin(0.374*time) + 0.40*sin(0.294*time) );
cy = cy + 0.210*( 0.60*sin(0.393*time) + 0.40*sin(0.223*time) );
dx = dx + 0.003*( 0.60*sin(0.234*time) + 0.40*sin(0.277*time) );
dy = dy + 0.003*( 0.60*sin(0.284*time) + 0.40*sin(0.247*time) );
decay = decay - 0.01*equal(frame%6,0);
dx = dx + dx_residual;
dy = dy + dy_residual;
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*0.96+1.3);
dx_residual = equal(bass_thresh,2.13)*0.016*sin(time*7) + (1-equal(bass_thresh,2.13))*dx_residual;
dy_residual = equal(bass_thresh,2.13)*0.012*sin(time*9) + (1-equal(bass_thresh,2.13))*dy_residual;
wave_x = wave_x - dx_residual*7;
wave_y = wave_y - dy_residual*7;
wave_mystery = time*0.03;

zoom = zoom + 0.008*(0.35*sin(0.844*time+2) + 0.55*sin(1.743*time+5));
//zoom = zoom + max(0,treb-1.2)*0.1;

q1 = 0.03;// + 0.09*pow( 0.5 + 0.25*sin(0.437*time+1) + 0.15*sin(0.197*time+4), 8 );

