waves.point_eqs_str:
sp = sample*6.28*8*8*4;
vol = (bass_att + mid_att + treb_att)*0.33;
vol = 0.2 + 0.5*(value1 + value2);
vol = .2;

mod = if(below(mid_att,1.8),mid_att+.2,2);

ox = 0.5*sin(sp)*sin(sample*3.14)*vol;
oy = (sample - 0)*mod;
oz = 0.5*cos(sp)*sin(sample*3.14)*vol;

xang = time*0.672;
axang = 0;
yang = time*-1.351;
ayang = 0;
zang = time*-0.401;
azang = 0;
fov = 0.6 + 0.2*sin(time);
fov = .5;

mx = ox*cos(zang) - oy*sin(zang);
my = ox*sin(zang) + oy*cos(zang);
ox = mx;
oy = my;
mx = ox*cos(yang) + oz*sin(yang);
mz = - ox*sin(yang) + oz*cos(yang);
ox = mx;
oz = mz;
my = oy*cos(xang) - oz*sin(xang);
mz = oy*sin(xang) + oz*cos(xang);
oy = my;
oz = mz;

oz = abs(oz) - 2;
x = ox*fov/oz + 0.5;
x = (x-.5)*0.75 + 0.5;
y = oy*fov/oz + 0.5;

r = 1 - sin(sp);
b = 0.5 - 0.5*sin(sample*1.57);
g = 0.5 - 0.5*cos(sample*1.57);
a = 0.5 + (oz + 2)*0.25;

