frame_eqs_eel:
warp=0;
le=1+.5+2*sin(bass_att);
bpulse=band(above(le,bth),above(le-bth,bblock));
bblock=le-bth;
bth=if(above(le,bth),le+114/(le+10)-7.407,
bth+bth*.07/(bth-12)+below(bth,2.7)*.1*(2.7-bth));
bth=if(above(bth,6),6,bth);
bres=bpulse*sin(pulse+le*.5) + bnot(bpulse)*bres;

le=1+.5+2*sin(treb_att);
tpulse=band(above(le,tth),above(le-tth,tblock));
tblock=le-tth;
tth=if(above(le,tth),le+114/(le+10)-7.407,
tth+tth*.07/(tth-12)+below(tth,2.7)*.1*(2.7-tth));
tth=if(above(tth,6),6,tth);
tres=tpulse*sin(pulse+le*.5) + bnot(tpulse)*tres;

le=1+.5+2*sin(mid_att);
mpulse=band(above(le,mth),above(le-mth,mblock));
mblock=le-mth;
mth=if(above(le,mth),le+114/(le+10)-7.407,
mth+mth*.07/(mth-12)+below(mth,2.7)*.1*(2.7-mth));
mth=if(above(mth,6),6,mth);
mres=mpulse*sin(pulse+le*.5) + bnot(mpulse)*mres;



pulse=if(above(abs(pulse),3.14),-3.14,pulse+(bth+mth+tth)*.003);
q1=bres;
q2=tres;
q3=mres;
q4=sin(pulse);

mod_state=(above(q1,0)+above(q2,0)+above(q3,0))*(1+above(q4,0));
ccl=ccl+tpulse+mpulse-bpulse;
q5=cos(pulse*(.5+.1*mod_state));
q6=sin(pulse*(.5+pow(.25,mod_state)));
q7=mod_state;
q8=ccl;

beat = if(equal(bres,prev_bass),0,1);
prev_bass = bres;
ying = if(below(ying,1),1,ying);
ying = if (beat,ying + bnot(time*10%3),ying);
yin = ying%2;
mytime = pulse*ying;
ib_size = 0.02;
ib_g = if(bnot(ying%11),ib_r,ib_b);
//ib_g = if(bnot(ying%11),1,0);
ib_r = if(bnot(ying%11),ying%3,ob_g);
ib_b = if(bnot(ying%11),ying%4,1);
mv_a = yin;
mv_b = sin(mytime) + abs(sin(mytime));
mv_r = cos(mytime);
mv_g = -.8*sign(sin(mytime))*abs(sin(mytime));
ob_b = cos(ying)  + abs(cos(ying));
ob_g = abs(sin(ying)) ;
ob_r = (-1 * cos(ying))  + abs(-1 * cos(ying)) + 0.2 * (cos(sin(ying))+(abs(cos(sin(ying)))+cos(sin(ying))));
q3 = if(beat,(10*abs(sin(mytime)))%3-1,0);
ib_size=.025+.02*q2;
ob_size=.03+.02*q3-.002*q7;
wave_r=.5+.5*sin(q1*q7+mytime*2.183);
wave_g=.5+.5*sin(q2*3+mytime*1.211);
wave_b=.5+.5*sin(q3+mytime*1.541);
dx = if(yin,dx -0.005*sin(mytime*0.23),if(equal(yin,yang),0,if(below(abs(dx),.05),dx-(q3*0.001),dx)));
dy = if(yin,dy - 0.005*sin(mytime*0.2),if(equal(yin,yang),0,if(below(abs(dy),.05),dy-(q3*0.001),dy)));
yang = bnot(yin);
ob_a=.8+.2*q2;
zoom=zoom+.04*q4;

warp:
shader_body { 
 float final_noise_1;
 vec3 tmpvar_2;
 tmpvar_2.xy = uv;
 tmpvar_2.z = (q2 * 10.0);
 final_noise_1 = ((texture (sampler_noisevol_hq, tmpvar_2) * 2.0) - 1.0).x;
 final_noise_1 = (final_noise_1 + (0.5 * (
   (texture (sampler_noisevol_hq, (tmpvar_2 * 2.0)) * 2.0)
  - 1.0)).x);
 final_noise_1 = (final_noise_1 + (0.25 * (
   (texture (sampler_noisevol_hq, (tmpvar_2 * 4.0)) * 2.0)
  - 1.0)).x);
 final_noise_1 = (final_noise_1 + (0.125 * (
   (texture (sampler_noisevol_hq, (tmpvar_2 * 8.0)) * 2.0)
  - 1.0)).x);
 final_noise_1 = ((final_noise_1 + 1.0) * 0.5);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = (texture (sampler_main, ((
   ((uv - 0.5) * final_noise_1)
  * 2.0) + 0.5)).xyz - 0.1);
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec2 uv_1;
 vec3 ret_2;
 uv_1 = (0.05 + (0.9 * uv));
 ret_2 = (abs((
   ((texture (sampler_blur2, uv_1).xyz * scale2) + bias2)
  - texture (sampler_main, uv_1).xyz)) * 6.0);
 ret_2 = (ret_2 * 0.85);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_2;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{

    float3 txr = float3(uv,q2*10);

    float final_noise = 0;


    final_noise += ((1.0/pow(2,0))*((tex3D(sampler_noisevol_hq, txr*pow(2,0))*2)-1)); 
    final_noise += ((1.0/pow(2,1))*((tex3D(sampler_noisevol_hq, txr*pow(2,1))*2)-1)); 
    final_noise += ((1.0/pow(2,2))*((tex3D(sampler_noisevol_hq, txr*pow(2,2))*2)-1)); 
    final_noise += ((1.0/pow(2,3))*((tex3D(sampler_noisevol_hq, txr*pow(2,3))*2)-1)); 

    final_noise =  (final_noise+1)*0.5;


    ret = tex2D( sampler_main, (uv-0.5)*final_noise*2 + 0.5 ).xyz; 

    // darken (decay) over time
    ret = ret - 0.1; //or try: ret -= 0.004;
}

