shader_body
{
    uv = 0.05 + 0.9*uv;
    ret = tex2D(sampler_main, uv).xyz;
      // SUPER GLOW EDGES - looks awesome w/octopus
      float3 avg_col = GetBlur2(uv);
      ret = abs(avg_col - ret)*6;
