warp_hlsl:
shader_body
{
    // sample previous frame
    ret.xyz = tex2D( sampler_main, uv ).xyz;

      // take the difference between the crisp and blurred images,
      // then add it back into the image.  Creates spots and stripes over time.
      float3 b0 = ret.xyz;
      float3 b1 = GetBlur1(uv).xyz;
      float3 b2 = GetBlur2(uv).xyz;
      //float b3 = GetBlur3(uv).xyz;
      float3 dc = b0-b1;
      dc = lerp(dc, (b2-b1)*2, rad);

      ret.xyz += dc*0.3;
      ret.xyz *= 0.9;
      ret = lerp(ret, 0.5, 0.03);


      // add noise:
      float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 1 + rand_frame.xy;
      ret.xyz += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0 * 122 
                    //* saturate(treb_att-1);
                    *0.16;

      // desaturate over time, to keep the globs white
      //ret.xyz = lerp(ret.xyz, lum(ret.xyz), 0.2);
      ret.xyz = lerp(ret.xyz, ret.zxy, 0.02);
}

