pixel_eqs_eel:
cpx = .5;
cpy = .5;
cpx = cpx + .5*sin(time);
cpy = cpy + .5*cos(time);
dx = dx + tan(x - cpx)*.04*pow(rad,3) - (x - cpx)+(y - cpy);
dy = dy + tan(y - cpy)*.04*pow(rad,3);

