frame_eqs_eel:
warp = 0;

tic = min(time-tin,.1);
tin = time;

ra = 1/tic*.25;

treb_avg = tic*(treb_avg*(1/tic - ra) + ra*treb);
mid_avg = tic*(mid_avg*(1/tic - ra) + ra*mid);
bass_avg = tic*(bass_avg*(1/tic - ra) + ra*bass);

tt = tt + tic*treb;
mt = mt + tic*mid;
bt = bt + tic*bass;

q1 = tt*4;
q2 = mt*4;
q3 = bt*4;

st = .1;
base = .15;
q4 = base + treb_avg*st;
q5 = base + mid_avg*st;
q6 = base + bass_avg*st;

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = texture (sampler_fc_main, uv).xyz;
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 uv3_1;
 vec3 noise_2;
 uv3_1.x = (0.5 - texture (sampler_noise_hq, vec2((uv.x + q4)))).x;
 uv3_1.y = (0.5 - texture (sampler_noise_hq, vec2((uv.y + q3)))).x;
 uv3_1.z = 1.0;
 uv3_1.x = (uv3_1.x * (0.5 * q2));
 uv3_1.y = (uv3_1.y * (0.5 * q1));
 noise_2 = (vec3(0.5, 0.5, 0.5) * normalize((texture (sampler_noise_hq, uv3_1.xy) * texture (sampler_main, uv))).xyz);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = (texture (sampler_main, noise_2.xy).xyz * (texture (sampler_noise_hq, noise_2.xy) + texture (sampler_fc_main, uv)).xyz);
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // darken (decay) over time
    ret *= 1.00; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
float3 ret2 = tex2D(sampler_main, uv);
float3 noise = .5;
float2 uv2 = 0;
float3 uv3 = 0;
float bl = GetBlur2(uv);
float3 foreground = 0;

uv2.x=1-tex2D(sampler_noise_hq, uv.x);
uv2.y=1-tex2D(sampler_noise_hq, uv.y);
uv2.x*=q1;
uv2.y*=q2;

uv3.x=.5-tex2D(sampler_noise_hq, uv.x+q4);
uv3.y=.5-tex2D(sampler_noise_hq, uv.y+q3);
uv3.z=1;

uv3.x*=.5*q2;
uv3.y*=.5*q1;

noise *= normalize(tex2D(sampler_noise_hq, uv3.xy-foreground)*tex2D(sampler_main, uv));

foreground=(tex2D(sampler_noise_hq, noise)+tex2D(sampler_fc_main, uv));

ret = tex2D(sampler_main, noise)*foreground;

}
