frame_eqs_eel:
warp = 0;

tic = min(time-tin,.1);
tin = time;

ra = 1/tic*.25;

treb_avg = tic*(treb_avg*(1/tic - ra) + ra*treb);
mid_avg = tic*(mid_avg*(1/tic - ra) + ra*mid);
bass_avg = tic*(bass_avg*(1/tic - ra) + ra*bass);

tt = tt + tic*treb;
mt = mt + tic*mid;
bt = bt + tic*bass;

q1 = tt*4;
q2 = mt*4;
q3 = bt*4;

st = .1;
base = .15;
q4 = base + treb_avg*st;
q5 = base + mid_avg*st;
q6 = base + bass_avg*st;

