init_eqs_eel:
db=.01;
beatrate=1;
avgbeatrate=2;
lbbtime=time;
avgdb=.01;
vol=1;
avgvol=.2;
phase=0;
bool=0;
const=.02;

mtime=1000+bass_att*1000;
t1=500+bass*500;
t2=500+treb*500;
t3=500+mid*500;


c1=0;
cx1=.5;
cy1=.5;
cx2=.5;
cy2=.5;

sw1=0;
sw2=0;
sw3=0;
sw4=0;
sw5=0;
sw6=0;
h=0;
hue=0;

frame_eqs_eel:
db=(bass-lbass)*fps;
lbass=bass;
avgdb=avgdb*.99+abs(db)*.01;
avgvol=avgvol*.99+(bass+mid+treb)*.0033;

//beatdection
rawbeatb=above(abs(db),avgdb*avgvol*5);
beatb=rawbeatb*above(time-lbbtime,avgbeatrate*.75);
beathard=beatb*(abs(db)-avgdb*4);
//beatrate calc
beatrate=beatb*(time-lbbtime)+(1-beatb)*beatrate;
avgbeatrate=beatb*(avgbeatrate*.9+beatrate*.1)+(1-beatb)*avgbeatrate;
lbbtime=time*beatb+(1-beatb)*lbbtime;

ph=(time-lbbtime)*(60/avgbeatrate)*const;
phase=max(min( ph, 1 ),0);

//const=beatb*( bool*(const-.001)+(1-bool)*(const+.001) ) + (1-beatb)*const;
const=const*(1-beatb)+beatb*( const+ (.01*bool) );

bool=below(phase,.98)*(.99-ph)-above(phase,.98)*(ph*.5);

bc=bc+beatb;


monitor=avgbeatrate;
vol=(rawbeatb*(abs(db)-(avgdb))*.01);
avgvol=avgvol*.999+vol*.01;

c1=(60/avgbeatrate)*vol*.001;

mtime=mtime+min(c1*vol+avgvol*.01,.15)*(60/fps);

zoom=1;
decay=1;
warp=0;
wrap=1;


// This is the version you should use...
// ----------- start copying -----------
hue=(mtime*.1+phase*.5); // change this
h=6*(hue-int(hue));
sw1=below(h,1); sw2=(1-sw1)*below(h,2); sw3=(1-sw1)*(1-sw2)*below(h,3); sw4=(1-sw1)*(1-sw2)*(1-sw3)*below(h,4);
sw6=above(h,5); sw5=(1-sw1)*(1-sw2)*(1-sw3)*(1-sw4)*(1-sw6);
ob_r=sw1+sw2*(2-h)+sw5*(h-4)+sw6;  // resulting Red value, assign it to q1
ob_g=sw1*h+sw2+sw3+sw4*(4-h);      // resulting Green value, assign it to q2
ob_b=sw3*(h-2)+sw4+sw5+sw6*(6-h);  // resulting Blue value, assign it to q3
// ----------- end ----------------

ib_r=0;
ib_g=0;
ib_b=0;




t1=t1+min(c1*bass+bass_att*.01,.15)*(60/fps);
t2=t2+min(c1*treb+treb_att*.01,.15)*(60/fps);
t3=t3+min(c1*mid+mid_att*.01,.15)*(60/fps);

q1=mtime;
q2=t1;
q3=t2;
q4=t3;

cx1=.5+.4*sin(mtime*.12-t4*.05);
cy1=.5+.4*sin(t1*.14-t2*.033);


cx2=.5+.4*sin(t2*.113-mtime*.05);
cy2=.5+.4*sin(t3*.13-t4*.001);

q5=cx1;
q6=cy1;

q7=cx2;
q8=cy2;

pixel_eqs_eel:
