frame_eqs_eel:
chng=sin(time*.5);
cthr=.9999;
mq21=if(above(chng,cthr),rand(3),mq21);
mq22=if(above(chng,cthr),rand(3),mq22);
mq23=if(above(chng,cthr),rand(3),mq23);
mq24=if(above(chng,cthr),rand(2),mq24);
mq25=if(above(chng,cthr),rand(2),mq25);
mq26=if(above(chng,cthr),rand(2),mq26);
mq27=if(above(chng,cthr),rand(1),mq27);
mq28=if(above(chng,cthr),rand(1),mq28);
mq29=if(above(chng,cthr),rand(1)*.3,mq29);
mq30=if(above(chng,cthr),rand(1)*.3,mq30);
monitor=chng;
q21=mq21;q22=mq22;q23=mq23;q24=mq24;q25=mq25;q26=mq26;
q27=mq27;q28=mq28;q29=mq29;q30=mq30;

monitor=mq1;
vol=bass+treb+mid;
atime=atime+vol;
q1=.4+sin(atime*.006        )*.4;
q2=.4+cos(atime*.00613828348)*.4;
q3=.4+sin(atime*.00598593455)*.4;
monitor=q3;
dec=pow(.98,60/fps);
num0=numbeat*(abs(num-num_att)+pump)+bnot(numbeat)*num0;
pump=numbeat*(sin(t)*.1+.1)+bnot(numbeat)*pump;
pump=pump*dec;

num_b0=num_b;
num=abs((bass-bass_att)+(mid-mid_att)+(treb-treb_att));
num_att=num_att*.95+num*.05;
num_b=above(num,1-pump)*above(abs(num-num_att),num0);
numbeat=bnot(equal(num_b0,num_b));

t=t+num*.1;
bc=bc+numbeat;
bass_flow=bass_flow+3*abs(bass-bass_att)/fps;
treb_flow=treb_flow+3*abs(treb-treb_att)/fps;
mid_flow=mid_flow+3*abs(mid-mid_att)/fps;

wave_r=.5+.5*sin(t*1.1+bc*2)+sin(bass_flow)*.25*(frame%2);
wave_g=.5+.5*sin(t*0.8+bc*3.14)+sin(treb_flow)*.25*(frame%2);
wave_b=.5+.5*sin(t*1.3)+sin(mid_flow)*.25*(frame%2);
wave_mystery=frame%2;

q1=t;
q2=bc;
q3=2+4*sin(t*0.33+time*1.42+bc*0.1);
q4=2+3*cos(t*0.15+time*1.41+bc*0.2);

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = texture (sampler_fc_main, uv).xyz;
 vec4 tmpvar_2;
 tmpvar_2.w = 0.0;
 tmpvar_2.xyz = ret_1;
 vec4 tmpvar_3;
 tmpvar_3 = max (tmpvar_2, (texture (sampler_fc_main, mix (uv_orig, uv, vec2(0.5, 0.5))) * 0.97));
 ret_1 = (tmpvar_3.xyz + ((tmpvar_3.xyz - 
   ((texture (sampler_blur1, uv).xyz * scale1) + bias1)
 ) * 0.15));
 ret_1 = (ret_1 * 0.97);
 vec4 tmpvar_4;
 tmpvar_4 = texture (sampler_noisevol_hq, (((vec3(0.05, 0.05, 0.0) * uv.xyy) * (
   (q27 * texsize.xyy)
  * texsize_noisevol_hq.zww)) + ((time * vec3(0.0, 0.0, 1.0)) * q29)));
 if (((ret_1.x > (q21 * q3)) && (ret_1.x <= (q24 * q1)))) {
   ret_1.y = (ret_1.y - (tmpvar_4.x * 0.5));
 };
 if (((ret_1.y > (q22 * q1)) && (ret_1.y <= (q25 * q2)))) {
   ret_1.z = (ret_1.z - (tmpvar_4.y * 0.5));
 };
 if (((ret_1.z > (q23 * q2)) && (ret_1.z <= (q26 * q3)))) {
   ret_1.x = (ret_1.x - (tmpvar_4.z * 0.5));
 };
 vec4 tmpvar_5;
 tmpvar_5.w = 1.0;
 tmpvar_5.xyz = ret_1;
 ret = tmpvar_5.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 vec3 tmpvar_2;
 tmpvar_2 = mix (texture (sampler_main, uv).xyz, texture (sampler_main, ((0.5 - uv) + 0.5)).xyz, vec3(0.4, 0.4, 0.4));
 ret_1 = (tmpvar_2 * (tmpvar_2 * tmpvar_2));
 ret_1 = (ret_1 + (0.05 * cos(
   ((dot (ret_1, vec3(0.32, 0.49, 0.29)) * 15.0) + 7.0)
 )));
 vec3 tmpvar_3;
 tmpvar_3 = mix (pow (ret_1, vec3(0.9, 0.5, 1.3)), ((
   ((texture (sampler_blur1, uv).xyz * scale1) + bias1)
  * 
   (1.0 - (rad * 1.2))
 ) * 0.7), vec3(rad));
 ret_1.z = tmpvar_3.z;
 ret_1.xy = tmpvar_3.zy;
 vec4 tmpvar_4;
 tmpvar_4 = texture (sampler_noisevol_hq, (((vec3(0.05, 0.05, 0.0) * uv.xyy) * (
   (q28 * texsize.xyy)
  * texsize_noisevol_hq.zww)) + ((time * vec3(0.0, 0.0, 1.0)) * q30)));
 if (((tmpvar_3.z > (q26 * q3)) && (tmpvar_3.z <= (q23 * q1)))) {
   ret_1.z = (tmpvar_3.z - (tmpvar_4.x * 0.5));
 };
 if (((tmpvar_3.y > (q25 * q1)) && (tmpvar_3.y <= (q22 * q2)))) {
   ret_1.x = (tmpvar_3.z - (tmpvar_4.y * 0.5));
 };
 if (((ret_1.z > (q24 * q2)) && (ret_1.z <= (q21 * q3)))) {
   ret_1.y = (tmpvar_3.y - (tmpvar_4.z * 0.5));
