warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;
    ret=max(ret,tex2D(sampler_fc_main,lerp(uv_orig,uv,0.5))*.97); //trails

    //add water effect
    ret+=(ret-GetBlur1(uv))*0.15;

    // darken (decay) over time
    ret *= 0.97; //or try: ret -= 0.004;

float4 noise9 = tex3D(sampler_noisevol_hq, 
                      ((uv.xyy*q27
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      float3(1,1,0)*0.05 + 
                      time*float3(0,0,1)*q29
                     );
if(ret.x> q21*q3         && ret.x<= q24*q1        )ret.y -= (noise9).x*.5; 
if(ret.y> q22*q1         && ret.y<= q25*q2        )ret.z -= (noise9).y*.5; 
if(ret.z> q23*q2         && ret.z<= q26*q3        )ret.x -= (noise9).z*.5; 
}

comp_hlsl:
shader_body
{
    float2 echo=(uv-0.5)*float2(-1,-1)+0.5;
    ret = tex2D(sampler_main, uv).xyz;
    ret=lerp(ret,GetPixel(echo),0.4);

    ret*=ret*ret; //darken 2x
    ret+=0.05*cos(lum(ret)*15+7); //some stripes...
    ret=pow(ret,float3(0.9,0.5,1.3)); //palletize
    ret=lerp(ret,GetBlur1(uv)*(1-rad*1.2)*0.7,rad); //focus
    ret.xy=ret.zy; //make the color back to green (with some purples)
float4 noise9 = tex3D(sampler_noisevol_hq, 
                      ((uv.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      float3(1,1,0)*0.05 + 
                      time*float3(0,0,1)*q30 
                     );
if(ret.x> q26*q3         && ret.x<= q23*q1        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q1         && ret.y<= q22*q2        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q2         && ret.z<= q21*q3        )ret.y -= (noise9).z*.5; 
}
