frame_eqs_eel:
dec=pow(.98,60/fps);
num0=numbeat*(abs(num-num_att)+pump)+bnot(numbeat)*num0;
pump=numbeat*(sin(t)*.1+.1)+bnot(numbeat)*pump;
pump=pump*dec;

num_b0=num_b;
num=abs((bass-bass_att)+(mid-mid_att)+(treb-treb_att));
num_att=num_att*.95+num*.05;
num_b=above(num,1-pump)*above(abs(num-num_att),num0);
numbeat=bnot(equal(num_b0,num_b));

t=t+num*.1;
bc=bc+numbeat;
bass_flow=bass_flow+3*abs(bass-bass_att)/fps;
treb_flow=treb_flow+3*abs(treb-treb_att)/fps;
mid_flow=mid_flow+3*abs(mid-mid_att)/fps;

wave_r=.75+.5*sin(t*1.1+bc*2)+sin(bass_flow)*.25*(frame%2);
wave_g=.4+.5*sin(t*0.8+bc*3.14)+sin(treb_flow)*.25*(frame%2);
wave_b=.65+.5*sin(t*1.3)+sin(mid_flow)*.25*(frame%2);

q1=t;
q2=bc;
q3=2+4*sin(t*0.33+time*1.42+bc*0.1);
q4=2+3*cos(t*0.15+time*1.41+bc*0.2);

pixel_eqs_eel:
rot=sin(rad*3.14+time)*sin(q2)*.3;
zoom=zoom-.05*sin(rad*1.5+q1);
cx=cx+.2*sin(x*2*q4+q1);
cy=cy+.2*sin(y*2*q3+time);
sx=sx+sin(y*3.14*q3+.76*q2)*(1.1-zoom);
sy=sy+sin(x*3.14*q4+1.25*q1)*(1.1-zoom);

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = texture (sampler_fc_main, uv).xyz;
 vec4 tmpvar_2;
 tmpvar_2.w = 0.0;
 tmpvar_2.xyz = ret_1;
 ret_1 = (max (tmpvar_2, (texture (sampler_fc_main, 
   mix (uv, uv_orig, vec2(0.25, 0.25))
 ) * 0.97)).xyz * 0.97);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2 = (1.0 - uv);
 ret_1 = (mix (texture (sampler_main, uv).xyz, texture (sampler_main, tmpvar_2).xyz, vec3(0.5, 0.5, 0.5)) * 1.67);
 ret_1 = ((ret_1 * (1.0 - ret_1)) * 4.0);
 ret_1 = (1.0 - mix (vec3(dot (ret_1, vec3(0.32, 0.49, 0.29))), ret_1, vec3(-0.3, -0.3, -0.3)));
 ret_1 = (ret_1 + (dot (
   (((texture (sampler_blur1, uv).xyz * scale1) + bias1) + ((texture (sampler_blur1, tmpvar_2).xyz * scale1) + bias1))
 , vec3(0.32, 0.49, 0.29)) / 1.5));
 vec3 tmpvar_3;
 tmpvar_3 = pow (ret_1, vec3(0.8, 1.5, 0.9));
 ret_1 = tmpvar_3;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = tmpvar_3;
 ret = tmpvar_4.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;
    ret=max(ret,tex2D(sampler_fc_main,lerp(uv,uv_orig,0.25))*.97); //trails

    // darken (decay) over time
    ret *= 0.97; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret=lerp(ret,GetPixel(1-uv),0.5);

    ret*=1.67;
    ret=ret*(1-ret)*4;
    ret=lerp(lum(ret),ret,-0.3);
    ret=1-ret;
    ret+=lum(GetBlur1(uv)+GetBlur1(1-uv))/1.5;
    ret=pow(ret,float3(0.8,1.5,0.9));
}
