num_att=num_att*.95+num*.05;
num_b=above(num,1-pump)*above(abs(num-num_att),num0);
numbeat=bnot(equal(num_b0,num_b));

t=t+num*.1;
bc=bc+numbeat;
bass_flow=bass_flow+3*abs(bass-bass_att)/fps;
treb_flow=treb_flow+3*abs(treb-treb_att)/fps;
mid_flow=mid_flow+3*abs(mid-mid_att)/fps;

wave_r=.5+.5*sin(t*1.1+bc*2)+sin(bass_flow)*.25*(frame%2);
wave_g=.5+.5*sin(t*0.8+bc*3.14)+sin(treb_flow)*.25*(frame%2);
wave_b=.5+.5*sin(t*1.3)+sin(mid_flow)*.25*(frame%2);
wave_mystery=frame%2;

q1=t;
q2=bc;
q3=2+4*sin(t*0.33+time*1.42+bc*0.1);
q4=2+3*cos(t*0.15+time*1.41+bc*0.2);

pixel_eqs_eel:
