warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;
float4 noise9 = tex3D(sampler_noisevol_hq, 
                      ((uv.xyy*q27
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      float3(1,1,0)*0.05 + 
                      time*float3(0,0,1)*q29
                     );
if(ret.x> q21*q13         && ret.x<= q24*q11        )ret.y += (noise9).x*.5; 
if(ret.y> q22*q11         && ret.y<= q25*q12        )ret.z += (noise9).y*.5; 
if(ret.z> q23*q12         && ret.z<= q26*q13        )ret.x += (noise9).z*.5; 
    ret=max(ret,tex2D(sampler_fc_main,lerp(uv_orig,uv,0.5))*.97); //trails

    //add water effect
    ret+=(ret-GetBlur1(uv))*0.15;

    // darken (decay) over time
    ret *= 0.97; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    float2 echo=(uv-0.5)*0.5*float2(-1,-1)+0.5;
    ret = tex2D(sampler_main, uv).xyz;
float4 noise9 = tex3D(sampler_noisevol_hq, 
                      ((uv.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      float3(1,1,0)*0.05 + 
                      time*float3(0,0,1)*q31
                     );
if(ret.x> q26*q13         && ret.x<= q23*q11        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q11         && ret.y<= q22*q12        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q12         && ret.z<= q21*q13        )ret.y -= (noise9).z*.5; 
    ret=lerp(ret,GetPixel(echo),0.3);

    //darken
    ret=1-ret;
    ret*=lum(ret);

    //blood-palletized
    ret=pow(ret,float3(0.2,1.2,0.8));
    ret=lerp(ret,float3(1,0,0),lum(ret));
    ret*=1.33;

    //add some precise edges here...
    ret-=(0.5-abs(ret.x-0.5))*2;
}
