frame_eqs_eel:
dec_med = pow (0.9, 30/fps);
dec_slow = pow (0.99, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .5+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %8;

q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass + mid + treb;

k1 =  is_beat*equal(index,0);
p1 =  k1*(p1+1) + (1-k1)*p1;

p2 = dec_med * p2+ (1-dec_med)*p1;
p3 = dec_med * p3+ (1-dec_med)*p2;
rott = p3 * 3.1416/2;
q27 = index + 1;

movz = movz + .001*30/fps*(1.5+sin(rott));
q29 = movz ;


movx = movx + .001*30/fps*(1+sin(time/7));
q28 = movx;


q30 = .5 * (sin(time/19)+1); 
q31 = 3*(sin(time/23)+2); 
q32 = sin(time/17)+1; 


fade = fade * dec_med + .98 * (1-dec_med);
q10 = fade;

