frame_eqs_eel:
warp = 0;
zoom = 1;
wave_a = 0;
w = w1; // forward vector angle
ww = -(y1-y2)*(1+(70/fps-1)*2) - y1*0.8; // bending indication
w = w - ww*5;
q1 = cos(ww);
q2 = sin(ww);
q3 = 1.12;
q4 = sin(w)*0.042;
q5 = cos(w)*0.042;
a = asin(1)*0.5;
d = 0.08;
q6 = cos(a+0*ww);
q7 = sin(a+0*ww);
q8 = 3.3;
q9 = cos(-w + asin(1))*d*aspectx;
q10 = sin(-w+asin(1))*d*aspecty;
q11 = cos(-a+0*ww);
q12 = sin(-a+0*ww);
q13 = q8;
q14 = cos(-w + asin(1))*d*aspectx;
q15 = sin(-w+asin(1))*d*aspecty;
bb = bb*0.97 + bass*0.04;
mm = mm*0.97 + mid*0.04;
tt = tt*0.97 + treb*0.04;
w1 = w1 + ww1*sqrt(vx*vx+vy*vy)*a*2;
ww1 = ww1*0.94 + y1*0.1;
q16 = (bb-tt)*0.2;
y1 = y1 + v1*0.1;
y2 = y2 + v2*0.2;
v1 = v1*0.95 - (y1-q16)*0.1;
v2 = v2*0.99 - (y2-y1)*0.2;
a = abs(y2-y1)*2.2/fps;
q17 = sin(w)*a;
q18 = -cos(w)*a;
v = 0.25;
x = x + vx*v; x=min(1,max(-1,x));
y = y + vy*v; y=min(1,max(-1,y));
vx = vx*0.97 +sin(w)*a  + (equal(x,-1)- equal(x,1))*0.2;
vy = vy*0.97 -cos(w)*a  + (equal(y,-1)- equal(y,1))*0.2;
q19 = x*0.5;
q20 = y*0.5;

q21 = -w1 - ww*4*0;

mx = max(max(bb,mm),tt);
mn = min(min(bb,mm),tt);
ob_r  = (bb-mn)/(mx-mn);
ob_g = (mm-mn)/(mx-mn);
ob_b = (tt-mn)/(mx-mn);
v = 1/fps;
xx = xx + v*(ob_r-ob_g);
yy = yy + v*(ob_g-ob_b);
q32 = xx;
q31 = yy;

pixel_eqs_eel:
