float3 d3d = float3(0.03,0.03,0);
float3 dx = tex3d(sampler_noisevol_hq, uv3d + float3(1,0,0)*d3d) - tex3d(sampler_noisevol_hq, uv3d - float3(1,0,0)*d3d);
float3 dy = tex3d(sampler_noisevol_hq, uv3d + float3(0,1,0)*d3d) - tex3d(sampler_noisevol_hq, uv3d - float3(0,1,0)*d3d);
float x =tex3d(sampler_noisevol_hq, uv3d);
float2 my_uv = uv + (-8*float2(dx.x,dy.x)*(1-x) - 8*float2(dy.x,-dx.x))*texsize.zw;

ret.x = tex2D( sampler_main, my_uv ).x;
ret.x += (ret.x - GetBlur3(my_uv))*0.1;
ret.x *= 0.95;
ret.x +=  rand*0.4*(1-x*2);

ret.y = GetPixel(uv_orig+rand*texsize.zw*0).y*0.96-0.004;

float2 d = texsize.zw*4;
dx = GetBlur1(uv + float2(1,0)*d) - GetBlur1(uv - float2(1,0)*d);
dy = GetBlur1(uv + float2(0,1)*d) - GetBlur1(uv - float2(0,1)*d);

float2 uv_blue =lerp(uv_orig,0.5+(my_uv-0.5)*1.02,-1);
uv_blue += float2(dx.z,dy.z)*texsize.zw*0 + rand*texsize.zw*4;
ret.z = GetPixel(uv_blue).z + ret.x*0.2;
ret.z += -(ret.z-GetBlur1(uv_blue).z)*0.1 - 0.03;
}

