waves.point_eqs_eel:
//plot x,y,z to point on circle
smp=sample*6.283;
xp=sin(smp )*0.10;
yp=cos(smp )*0.10;
zp=0;


//alter shape;
angy=sin(sample*6.28*4 +t1 )*6.28;
xq=xp*cos(angy) - zp*sin(angy);
zq=xp*sin(angy) + zp*cos(angy);
xp=xq;
zp=zq;


//rotate on y axis;
angy=t1*0.1;
xq=xp*cos(angy) - zp*sin(angy);
zq=xp*sin(angy) + zp*cos(angy);
xp=xq;
zp=zq;

//rotate on x axis
axs1 = sin(t1*0.15) + 1.6;
yq= yp*cos(axs1) - zp*sin(axs1);
zq= yp*sin(axs1) + zp*cos(axs1);
yp=yq;
zp=zq;

//rotate on y axis again
axs2 = sin(t1*0.1)*3.3;
xq=xp*cos(axs2) - zp*sin(axs2);
zq=xp*sin(axs2) + zp*cos(axs2);
xp=xq;
zp=zq;

//stretch y axis to compensate for aspect ratio
yp=yp*1.2;

//push forward into viewpace
zp=zp+2.1;

//project x,y,z into screenspace
xs=xp/zp;
ys=yp/zp;

//center 0,0 in middle of screen
x=xs+0.5+q4;
y=ys+0.5+q5;

r=1-q1;
g=1-q2;
b=1-q3;

frame_eqs_eel:
wave_a = 0;



ib_r=sin(time)*0.5+0.5;
ib_g=sin(time+2.1)*0.5+0.5;
ib_b=sin(time+4.2)*0.5+0.5;

ob_r=ib_r-0.5;
ob_g=ib_g-0.5;
ob_b=ib_b-0.5;
q1=ib_r;
q2=ib_g;
q3=ib_b;



decay = .98;


//echo_orient=((bass_att+mid_att+treb_att)/3)*3;
//solarize=above(0.5,bass);
//darken=above(0.4,treb);

musictime=musictime+(mid*mid)*0.003*(75/fps);
basstime=basstime+(bass*bass)*0.03*(75/fps);


xpos=sin(musictime*0.6)*0.1;
ypos=sin(musictime*0.4)*0.3;
q4=xpos;
q5=ypos;
q6=basstime;

