sx=zm;
sy=zm;
rot=above(rd,0.7)*(rd-0.7)*sin(time*0.3)*0.6 * -sin(ag*4);

dx=sin(y*40)*(q4*0.05);
dy=cos(x*40)*(q4*0.05);

