dec_slow = pow (0.95, 30/fps);
//dec_xlow = pow (0.999, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, -.2+avg+peak) * above (time, t0+.1);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %32;
index2 = (index2 + is_beat*bnot(index))%4;
index3 = (index2 +index3 + is_beat*bnot(index))%4;

is_beat2 = above (time, t1+.1);
t1 = is_beat*time + (1-is_beat)*t1;
q22 = peak;
q24 = is_beat;
q26 = bass_att + mid_att + treb_att;
vol = vol*dec_med + (1-dec_med)*q26;
q27 = index + 1.5;
q28 = index2 +1;

p2 = .3*sin(time) + .3*cos(time/3);
//*****tunnel tilting
q1 = cos(p2 * 3.1416/4);
q2 = sin(p2 * 3.1416/4);
q3 = -q2;
q4 = q1;

//****tunnel viewpoint x,y
vtrig = is_beat * bnot(index%2);
vx = vx * bnot(vtrig) + vtrig * (rand(100)/100-.5);
vy = vy * bnot(vtrig) + vtrig * (rand(100)/100-.5);
vz = vz * bnot(vtrig) + vtrig * (rand(100)/100-.5);

vx = vx* below(index%32,24); //###
vy = vy* above(index%32,16);
q5 = vx*5;
q6 = vy*5;

//***tunnel width
q21 = (1+sin(time))/4+.05;

//**floor proximity
prox = prox - above(vy,.3)*prox*2;
q32 = .5 + .2*prox;
monitor = prox;

//****tunnel rotation
rota = rota +.02*below(index%16,8)/fps*30;
rota1 = rota1*dec_med + (1-dec_med)*rota;
q7 = rota1;

//****tunnel speed
movez = movez + .8/(1+abs(vx)+abs(vy))/fps;
q8 = movez * (1+q21/400);
//dyn. Aenderung von q21 bewirkt auch Richt.wechsel

q15 = -1+8*above(index,26);
//***********************
vxs = vxs * dec_med + (1-dec_med)*vx*20;
vys = vys * dec_med + (1-dec_med)*vy*20;
vzs = vzs * dec_med + (1-dec_med)*vz*20;

z0 = 20+vzs;

p1z = z0;
q9 = vxs/(p1z); 
q10 = vys/(p1z); 
q11 = p1z;

q12 = below (index,24);
q13 = above (index,16);

