shader_body
{

    //rotate texture cords
    float texRotU2 = (uv[0] - 0.5)*cos(-q8*0.29) + (uv[1] - 0.5)*sin(-q8*0.29);
    float texRotv2 = -(uv[0] - 0.5)*sin(-q8*0.29) + (uv[1] - 0.5)*cos(-q8*0.29);


	    float2 uv2 = float2(texRotU2*0.8 +0.5, texRotv2*0.8 + 0.5);

    float2 uv_echo = (uv - 0.5)*1.000*float2(-1,-1) + 0.5;
    ret = lerp( tex2D(sampler_main, uv).xyz, 
                tex2D(sampler_main, uv_echo).xyz, 
                0.50 
              )*0.6;

    ret += tex2D(sampler_main, uv2).xyz*0.4;
