shapes.frame_eqs_eel:
shpt = above(bass_att,shpt)*2+(1-above(bass_att,shpt))*((shpt-1.3)*0.96+1.3);
sdx_r = equal(shpt,2)*0.015*sin(5*time)+(1-equal(shpt,2))*sdx_r;
sdy_r = equal(shpt,2)*0.015*sin(6*time)+(1-equal(shpt,2))*sdy_r;

a1x = .5 + 0.42*cos(1-sin(3.2*time));
a1y = .5 + 0.47*sin(0.72*time-cos(1.1*time));
a2y = .5 + 0.42*cos(1-sin(1.82*time));
a2x = .5 + 0.47*sin(0.72*time-cos(1.72*time));
a3x = .5 + 0.42*sin(1-cos(2.12*time));
a3y = .5 + 0.47*cos(0.72*time-sin(2.7*time));
a4y = .5 + 0.42*sin(1-cos(1.72*time));
a4x = .5 + 0.47*cos(0.72*time-sin(1.2*time));


sg1 = abs(sin(time));
sg2 = a1x*above(sg1,0.75) + a2x*below(sg1,0.25) + a3x*above(sg1,0.25)*below(sg1,0.5)+
a4x*above(sg1,0.5)*below(sg1,0.75);
sg3 = if(above(shpt,1.3),sg2,sg3);
sg4 = a1y*above(sg1,0.75) + a2y*below(sg1,0.25) + a3y*above(sg1,0.25)*below(sg1,0.5)+
a4y*above(sg1,0.5)*below(sg1,0.75);
sg5 = if(above(shpt,1.3),sg4,sg5);


x = sg3;
y = sg5;
border_r = q1;
border_b = q2;
border_g = q3;

waves.point_eqs_eel:
r = q1;
g = q2;
b = q3;

x = x + 0.42*cos(1-sin(3.2*time));
y = y + 0.47*sin(0.72*time-cos(1.1*time));

r = 1-abs(q1);
g = 1-abs(q2);
b = 1-abs(q3);

y = y + 0.42*cos(1-sin(1.82*time));
x = x + 0.47*sin(0.72*time-cos(1.72*time));

r = q3;
g = q1;
b = q2;

y = y + 0.42*sin(1-cos(1.72*time));
x = x + 0.47*cos(0.72*time-sin(1.2*time));

pixel_eqs_eel:
zoom = zoom + rad*0.1;
rot = rot +0.001+ x/15*sin(bass*ang*rad*time);
warp = warp + 4*sin (bass*ang*rad*time);

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_main, (uv + (
   (texture (sampler_main, (uv + texsize.zw)).xy - 0.37)
  * 0.03))).xyz - 0.004);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = texture (sampler_main, (((texture (sampler_blur2, uv).xyz * scale2) + bias2).x * texture (sampler_main, uv).xyz).xy).xyz;
 ret = tmpvar_1.xyz;
}

warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 uv2 = uv;
    const float mipbias = 0;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, uv2 + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.37) * 0.03;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;

    float bl = GetBlur2(uv);
    float2 texc = bl*ret.xyz;

    ret = tex2D(sampler_main, texc);
}
