warp_hlsl:
shader_body
{
//ret=GetPixel(uv_orig+texsize.zw*float2(q6,q7)); 
float2 newuv=uv;
ret=GetPixel(newuv);
float3 lum1=0,lum2=0,lum3=0,lum4=0,lum5=0,lum6=0,lum7=0,lum8=0;
float3 mxv=0;
float r=8;
//Blur1: r=4 (pixels);
//Blur2: r=8 (pixels);
//Blur3: r=9 (pixels);
float ro=0.707106*r;
texsize.zw*r;
lum1=GetBlur2(newuv+texsize.zw*float2(1,0));
lum2=GetBlur2(newuv+texsize.zw*float2(0,1));
lum3=GetBlur2(newuv+texsize.zw*float2(-1,0));
lum4=GetBlur2(newuv+texsize.zw*float2(0,-1));
lum5=GetBlur2(newuv+texsize.zw*float2(ro,-ro));
lum6=GetBlur2(newuv+texsize.zw*float2(-ro,ro));
lum7=GetBlur2(newuv+texsize.zw*float2(ro,ro));
lum8=GetBlur2(newuv+texsize.zw*float2(-ro,-ro));
mxv=abs(lum1-lum2+lum3-lum4+lum5-lum6+lum7-lum8)*0.5;
ret*=mxv+0.99;
ret=ret-mxv*2+0.1+0.1*(0.15-tex2D(sampler_noise_lq,uv*rand_frame.xy*9))-0.1*GetBlur1(uv);
}

