shader_body
{

    float1 d = 0.01;
    float1 dx = ( GetBlur2(uv + float2(d,0)) - GetBlur2(uv-float2(d,0)) ).y*1280*texsize.z;
    float1 dy = ( GetBlur2(uv + float2(0,d)) - GetBlur2(uv-float2(0,d)) ).y*1024*texsize.w;
    float2 my_uv = uv + float2(dx,dy)*0.005;



//    float1 v = 0.01;
    ret.y = tex2D( sampler_fw_main, my_uv).y;


//ret = 0;
//   ret = tex2D( sampler_cells, uv)/ lerp(ret,tex2D(sampler_main,uv_orig-my_uv+uv),0.3);
      // take the difference between the crisp and blurred images,
      // then add it back into the image.  Creates spots and stripes over time.
      ret.xyz += (ret - GetBlur3(uv-floor(uv)*0))*0.3 - 0.05;
      ret.xyz *= 1;

      // add noise:
      float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.25 + rand_frame.xy;
      ret.xyz += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)*0.1;

      // desaturate over time, to keep the globs white
      ret.xyz = lerp(ret.xyz, lum(ret.xyz), 0.2);
}

