warp:
shader_body { 
 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2 = (uv - 0.5);
 ret_1.x = texture (sampler_main, (tmpvar_2 + 0.5)).x;
 ret_1.y = texture (sampler_main, ((tmpvar_2 * 0.98) + 0.5)).y;
 ret_1.z = texture (sampler_main, ((tmpvar_2 * 0.96) + 0.5)).z;
 ret_1 = (ret_1 - (ret_1 * 0.5));
 ret_1 = ((ret_1 * abs(
   (((texture (sampler_blur2, uv).xyz * scale2) + bias2) - 0.5)
 )) * 5.0);
 ret_1 = (ret_1 - 0.01);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 float ang2_1;
 ang2_1 = ((ang * 0.1591549) + (time * 0.025));
 float tmpvar_2;
 tmpvar_2 = (3.0 + floor((rand_preset.z * 5.95)));
 ang2_1 = (fract((ang2_1 * tmpvar_2)) / tmpvar_2);
 ang2_1 = (abs((ang2_1 - 
   (0.5 / tmpvar_2)
 )) * 6.283185);
 vec2 tmpvar_3;
 tmpvar_3.x = cos(ang2_1);
 tmpvar_3.y = sin(ang2_1);
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = (texture (sampler_main, (0.5 + (
   ((0.4 * (rad * sqrt(
     dot (texsize.xy, texsize.xy)
   ))) * tmpvar_3)
  * texsize.zw))).xyz * 1.333);
 ret = tmpvar_4.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;
    float3 ret2 = tex2D( sampler_fc_main, uv ).xyz;

    ret.x = tex2D( sampler_main, (uv-0.5)*1.00+0.5 ).x;
    ret.y = tex2D( sampler_main, (uv-0.5)*0.98+0.5 ).y;
    ret.z = tex2D( sampler_main, (uv-0.5)*0.96+0.5 ).z;   

    // darken (decay) over time
    ret = ret- (ret*.5);
    ret = ret*(abs(GetBlur2(uv)-.5))*5;
    ret = ret - .01;
}

comp_hlsl:
shader_body
{
    float rad_lq = rad * length(texsize.xy)*0.5;
    float ang_lq = ang;
    float2 uv_temp1 = (uv - 0.5) * texsize.xy;
    float rad_hq = length(uv_temp1);
    float ang_hq = atan2(-uv_temp1.y, uv_temp1.x);

    float2 uv2 = (uv - 0.5);
    float rad2 = rad_lq*0.8;
    float ang2 = ang_lq * M_INV_PI_2;

        // ROTATION OVER TIME
        #if 1
          ang2 += time*0.025;   //rotate over time
        #endif

        // FIN TYPE
        float fins = 3 + floor(rand_preset.z*5.95);
        #if 1     // SHARP FINS
          ang2 = frac(ang2*fins)/fins;
              // ENABLE THIS TO MAKE THE FINS ALTERNATE: (seamless!)
              ang2 = abs(ang2 - 0.5/fins);
        #else     // COSINE FINS - a bit slow
          ang2 = cos(ang2*M_PI_2*fins) * 0.023;
        #endif

        // RADIAL KALEIDOSCOPING?
        #if 0
          rad2 *= g_fTexSize.z;
            // choose one:
            //rad2 = lerp(rad2, frac(rad2*3)/3.0, 0.5);
            rad2 = cos(rad2*5);
            //rad2 = sqrt(rad2)*0.5;
            //rad2 += abs(frac(rad2*7)-0.5)/7.0 * 1.15;  // stepifier
            //rad2 += cos(rad2*61)*0.02;
          rad2 *= g_fTexSize.x;
        #endif

    ang2 *= M_PI_2;
    uv2 = 0.5 + rad2*float2(cos(ang2),sin(ang2))*texsize.zw;

    ret = tex2D(sampler_main, uv2).xyz;
    ret *= 1.333; // a little bit of overbright
}
