warp_hlsl:
shader_body
{

    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw*2 + rand_frame.xy;
    uv+= (tex2D(sampler_noise_lq, dither_uv).xy-0.5)*texsize.zw;

    // PAINTERLY EFFECT:
    float2 t = -0.004 + 0.04*saturate(float2(bass,treb)-1);
    float2 uv2 = uv;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, 
        lerp(uv,uv_orig,-1) + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.4) * t;
    //uv2.xy -= (blurry_color.zx-0.37) * s;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.0008 + (tex2D(sampler_noise_lq, dither_uv)-0.5)*0.02;    
}

comp_hlsl:
