w1 = q3; // first rotation: clockwise in the desktop pane
w2 = q4; // rotation around vertical axis
w3 = q5; // rotation around horizontal axis


// definition of the 3D shape

i = instance;

my_x = (i % 8)/8 - 0.5;
my_y = (i % 64 - (i % 8))/64 - 0.5;
my_z = (i - (i % 64 - (i % 8))/64)/512 - 0.5;


// 3D rotations

x1 = cos(w1)*my_x + sin(w1)*my_y;
y1 = -sin(w1)*my_x + cos(w1)*my_y;
z1 = my_z;

x2 = cos(w2)*x1 + sin(w2)*z1;
z2 = -sin(w2)*x1 + cos(w2)*z1;
y2 = y1;

y3 = cos(w3)*y2 + sin(w3)*z2;
z3 = -sin(w3)*y2 + cos(w3)*z2;
