{


    float3 rc = GetPixel(uv)+GetBlur1(uv);//+GetPixel(uv);
    float2 ruv = 0.5 + (uv-0.5)*(1+(rc.y*0.03));  

    ruv = frac(float2(ruv.x+pow(rc.x,q18), ruv.y+pow(rc.x, q17)));


    ret = tex2D(sampler_fc_main, float2(ruv.x, ruv.y)).xyz;

    float3 noise2 = lerp(float3(0,0,0),float3(q10,q11,q12), ret.x);    


    ret += saturate(cross((ret), (noise2)));
