frame_eqs_eel:
dec_med = pow (0.96, 30/fps);
dec_slow = pow (0.99, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .2+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %4;
index2 = (index2 + is_beat*bnot(index))%8;
index3 = (index3 + is_beat*bnot(index)*bnot(index2))%3;


q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass + mid + treb;

//k1 =  is_beat*bnot(index)*bnot(index2);

k1 =  is_beat*equal(index,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
p3 = dec_med * p3+ (1-dec_med)*p2;
rott = p3*3.1416/4;

rot1 = rot1 + q26;
q25 = .01*rot1;

q27 = 8-index;
q28 = index3;

dx1 = dec_med*dx1 + (1-dec_med)*bnot(index2);
q29 = dx1;
monitor = q29;

q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;


q5 = 1.5*cos(time/9);
q6 = .5*sin(time/7);

clear = clear *dec_med + 1-dec_med;
q32 = clear;

mov = bnot(q24)*movex + (int(rand(100))/100-50)*q2;
movex = movex + .2/fps *q2;
q18 = movex;

warp:
shader_body { 
 vec3 noise3_1;
 vec3 tmpvar_2;
 tmpvar_2 = (texture (sampler_main, uv).xyz + ((texture (sampler_blur1, uv).xyz * scale1) + bias1));
 vec2 tmpvar_3;
 tmpvar_3 = (0.5 + ((
   (uv - vec2(0.0, 1.0))
  - 0.5) * (1.0 + 
   (tmpvar_2.y * 0.03)
 )));
 vec2 tmpvar_4;
 tmpvar_4.x = (tmpvar_3.x + pow (tmpvar_2.x, 0.0));
 tmpvar_4.y = (tmpvar_3.y + pow (tmpvar_2.x, 0.005));
 noise3_1 = (texture (sampler_noise_lq, ((
   (uv_orig * texsize.xy)
  * texsize_noise_lq.zw) + rand_frame.xy)).xyz * fract(q15));
 vec3 tmpvar_5;
 tmpvar_5 = (noise3_1 * (vec3(1.0, 1.0, 1.0) - vec3(fract(
   (q3 * 0.5)
 ))));
 noise3_1 = tmpvar_5;
 vec4 tmpvar_6;
 tmpvar_6 = texture (sampler_main, fract(tmpvar_4));
 vec3 tmpvar_7;
 tmpvar_7.x = q10;
 tmpvar_7.y = q11;
 tmpvar_7.z = q12;
 vec3 tmpvar_8;
 tmpvar_8 = mix (tmpvar_5, tmpvar_7, tmpvar_6.xxx);
 vec4 tmpvar_9;
 tmpvar_9.w = 1.0;
 tmpvar_9.xyz = (tmpvar_6.xyz + clamp ((
   (tmpvar_6.yzx * tmpvar_8.zxy)
  - 
   (tmpvar_6.zxy * tmpvar_8.yzx)
 ), 0.0, 1.0));
 ret = tmpvar_9.xyz;
}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_main, uv).xyz + ((texture (sampler_blur2, uv).xyz * scale2) + bias2));
 ret = tmpvar_1.xyz;
}

warp_hlsl:
shader_body
{


    float3 rc = GetPixel(uv)+GetBlur1(uv);//+GetPixel(uv);
    uv = uv-float2(0,1);
    float2 ruv = 0.5 + (uv-0.5)*(1+(rc.y*0.03));  

    ruv = frac(float2(ruv.x+pow(rc.x,0), ruv.y+pow(rc.x, 0.005)));

    float3 noise3 = tex2D(sampler_noise_lq, uv_orig*texsize.xy*texsize_noise_lq.zw+rand_frame.xy);
    noise3 *= frac(q15);
    noise3 = lerp(noise3, float3(0,0,0), frac(q3*0.5));

    ret = tex2D(sampler_main, float2(ruv.x, ruv.y)).xyz;

    float3 noise2 = lerp(noise3,float3(q10,q11,q12), ret.x);    

    ret += saturate(cross((ret), (noise2)));
    ret *= 1; 


}

