frame_eqs_eel:
dec_med = pow (0.7, 30/fps);
dec_slow = pow (0.99, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .3+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %16;
index2 = (index2 + is_beat*bnot(index))%2;

q20 = avg;
q21 = beat;
q22 = peak;
q24 = is_beat;
q26 = bass_att + mid_att + treb_att;
q27 = index + 1;
q28 = index2;

k1 =  is_beat*equal(index%2,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott = p2 * 3.1416/8;

q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;

//ds = max ((q26-.2),0);
//step = step + ds/10;

step = step + q24 ;

p3 = p3*dec_slow + (1-dec_slow) * step;
q30 = step;

p4 = dec_slow*p4 + (1-dec_slow)*q27;
q31 = p4;

q12 = time -t0;
monitor = q12;



zoom = 1.2+q1*.2;
rot = -0 ;
dx = .00;

warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz + ((tmpvar_2.xyz - 
   ((texture (sampler_blur2, uv).xyz * scale2) + bias2)
 ) * 0.6));
 ret_1 = (ret_1 * 0.9);
 ret_1 = (ret_1 + ((
   ((texture (sampler_noise_lq, ((
     (uv_orig * texsize.xy)
    * 
     (texsize_noise_lq.zw * 0.4)
   ) + rand_frame.xy)).xyz - 0.5) / 256.0)
  * 12.0) * clamp (
   (treb_att - 1.0)
 , 0.0, 1.0)));
 vec3 tmpvar_3;
 tmpvar_3 = mix (ret_1, vec3(dot (ret_1, vec3(0.32, 0.49, 0.29))), vec3(0.08, 0.08, 0.08));
 ret_1 = tmpvar_3;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = tmpvar_3;
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (mix (texture (sampler_main, uv).xyz, texture (sampler_main, (
   (vec2(-0.993, 0.993) * (uv - 0.5))
  + 0.5)).xyz, vec3(0.5, 0.5, 0.5)) * (1.0 + (treb_att + 
   (mid_att * 0.5)
 )));
 vec3 tmpvar_2;
 tmpvar_2 = sqrt(ret_1);
 ret_1 = (tmpvar_2 * (tmpvar_2 + (treb_att * 0.05)));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // take the difference between the crisp and blurred images,
    // then add it back into the image.  Creates spots and stripes over time.
    ret += (ret - GetBlur2(uv))*0.6;
    ret *= 0.9;

   // add noise:
   float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
   ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0 * 12 * saturate(treb_att-1);

   // desaturate over time, to keep the globs white
   ret = lerp(ret, lum(ret), 0.08);
}

comp_hlsl:
shader_body
{
    float2 uv_echo = (uv - 0.5)*0.993*float2(-1,1) + 0.5;
    ret = lerp( tex2D(sampler_main, uv).xyz, 
                tex2D(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo
    ret *= 1.00 + (treb_att + (mid_att * .5)); //gamma
    ret = sqrt(ret); //brighten
    ret *= ret + (treb_att * .05); //darken
