frame_eqs_eel:
dec_med = pow (0.6, 30/fps);
dec_slow = pow (0.9, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .2+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %4;
index2 = (index2 + is_beat*bnot(index))%4;
index3 = (index3 + is_beat*bnot(index)*bnot(index2))%3;


q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = (bass_att + mid_att + treb_att)/3+1;

movez = movez + .1*(1+.3*q26)*30/fps;
q30 = movez;

//k1 =  is_beat*bnot(index)*bnot(index2);

k1 =  is_beat*equal(index,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_slow * p2+ (1-dec_slow)*p1;
p3 = dec_slow * p3+ (1-dec_slow)*p2;
rott = p3*3.1416/4;


q27 = 8-index;
q28 = index2+1;


q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;

mox = mox + .03*30/fps;
moy = moy + .04*30/fps;
q5 = mox;
q6 = moy;


mv_l = q24/2+.1;
dx = .0*q1;

warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz + ((tmpvar_2.xyz - 
   ((texture (sampler_blur2, uv).xyz * scale2) + bias2)
 ) * 0.3));
 ret_1 = (ret_1 * 0.9);
 ret_1 = (ret_1 + ((
   ((texture (sampler_noise_lq, ((
     (uv_orig * texsize.xy)
    * 
     (texsize_noise_lq.zw * 0.4)
   ) + rand_frame.xy)).xyz - 0.5) / 256.0)
  * 122.0) * clamp (
   (treb_att - 1.0)
 , 0.0, 1.0)));
 vec3 tmpvar_3;
 tmpvar_3 = mix (ret_1, vec3(dot (ret_1, vec3(0.32, 0.49, 0.29))), vec3(0.2, 0.2, 0.2));
 ret_1 = tmpvar_3;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = tmpvar_3;
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 0.25));
 ret_1 = ret_1.yyy;
 ret_1 = (pow (ret_1, vec3(0.8, 0.4, 1.7)) * 1.9);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // take the difference between the crisp and blurred images,
    // then add it back into the image.  Creates spots and stripes over time.
    ret += (ret - GetBlur2(uv))*0.3;
    ret *= 0.9;

   // add noise:
   float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
   ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0 * 122 * saturate(treb_att-1);

   // desaturate over time, to keep the globs white
   ret = lerp(ret, lum(ret), 0.2);
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret += GetBlur1(uv)*0.25;
    ret = ret.yyy;
    ret = pow(ret, float3(0.8,0.4,1.7))*1.9;
}
