shader_body
{
    ret = tex2D(sampler_main, (uv-0.5)*0.6+0.5).zzz;

    float crisp, blur;
    float2 uv2;

    float shadow_offset = 0.0;
    float shadow_str = 1.15;

    uv2 = (uv-0.5)*0.8+0.5;
    crisp = tex2D(sampler_main, uv2).y;
      // add drop shadow:
      blur  = GetBlur1(uv2 + shadow_offset).y;
      ret *= (1-blur*shadow_str);
