dv = (y*2-1) + (q5+q8+q11-1.5)*2/3;
dist = sqrt(du*du+dv*dv);
ang2 = atan2(du,dv);
mult = sin(dist*0.05)*0.4*q14;
dx = dx+mult*sin(ang2*2+q13)*aspectx;
dy = dy_mult*cos(ang2*2+q13)*aspecty;
cx1 = 0.5+sin(time*0.618)*0.2;
cy1 = 0.5+cos(time*1.618)*0.2;
dir = bass;
d = sqrt((x-cx1)*(x-cx1)+(y-cy1)*(y-cy1));
x1 = if( above(d,0.3),0,  sin(y-cy1)*0.05*dir);
