waves.point_eqs_eel:
//plot x,y,z to point on circle
smp=sample*6.283;
xp=sin(smp )*0.10;
yp=cos(smp )*0.10;
zp=0;


//alter shape;
angy=sin(sample*6.28*4 +t1 )*6.28;
xq=xp*cos(angy) - zp*sin(angy);
zq=xp*sin(angy) + zp*cos(angy);
xp=xq;
zp=zq;


//rotate on y axis;
angy=t1*0.1;
xq=xp*cos(angy) - zp*sin(angy);
zq=xp*sin(angy) + zp*cos(angy);
xp=xq;
zp=zq;

//rotate on x axis
axs1 = sin(t1*0.15) + 1.6;
yq= yp*cos(axs1) - zp*sin(axs1);
zq= yp*sin(axs1) + zp*cos(axs1);
yp=yq;
zp=zq;

//rotate on y axis again
axs2 = sin(t1*0.1)*3.3;
xq=xp*cos(axs2) - zp*sin(axs2);
zq=xp*sin(axs2) + zp*cos(axs2);
xp=xq;
zp=zq;

//stretch y axis to compensate for aspect ratio
yp=yp*1.2;

//push forward into viewpace
zp=zp+2.1;

//project x,y,z into screenspace
xs=xp/zp;
ys=yp/zp;

//center 0,0 in middle of screen
x=xs+0.5+q4;
y=ys+0.5+q5;

r=1-q1;
g=1-q2;
b=1-q3;

frame_eqs_eel:
wave_r = wave_r + 0.400*( 0.60*sin(0.933*time) + 0.40*sin(1.045*time) );
wave_g = wave_g + 0.400*( 0.60*sin(0.900*time) + 0.40*sin(0.956*time) );
wave_b = wave_b + 0.400*( 0.60*sin(0.910*time) + 0.40*sin(0.920*time) );
q8 = oldq8+if(above(bass+bass_att,1.8),q8+0.0005*pow((bass+bass_att-1),9),0);
oldq8 = q8;
monitor = q8;
zoom = zoom + 0.023*( 0.60*sin(0.339*q8) + 0.40*sin(0.276*q8) );
rot = rot + 0.030*( 0.60*sin(0.381*q8) + 0.40*sin(0.579*q8) );
//decay = decay - 0.02*equal(frame%40,0);
mv_r = wave_r;
mv_b = wave_b;
mv_g = wave_g;
mv_x = 1.25;
mv_y = 1.25;
mv_dx = 0.1*sin(1.1*time);
mv_dy = 0.1*cos(1.112*time);

