    float2 d = texsize.zw*4;
    float3 dx = (2*GetBlur1(frac(uv+float2(1,0)*d))-2*GetBlur1(frac(uv-float2(1,0)*d)))*0.5;
    float3 dy = (2*GetBlur1(frac(uv+float2(0,1)*d))-2*GetBlur1(frac(uv-float2(0,1)*d)))*0.5;
    float3 b1 = GetBlur1(uv) - 0;
