frame_eqs_eel:
beatb=above(bass,bass_att);
beatbhard=if(beatb,bass-bass_att*.9,beatbhard);

beatm=above(mid,mid_att);
beatmhard=if(beatm,mid-mid_att*.9,beatmhard);

beatt=above(treb,treb_att);
beatthard=if(beatt,treb-treb_att*.9,beatthard);

vol=bass+mid+treb;
vol_att=bass_att+mid_att+treb_att;


q1=beatb;
q2=beatbhard;
q3=beatm;
q4=beatmhard;
q5=beatt;
q6=beatthard;

wrap=1;
decay=.9+q1*.05+q5*.05;

gg=gg+q1*.03*q2;
bb=bb+q3*.03*q4;
rr=rr+q5*.03*q6;

gg=if(above(gg,1),0,if(below(gg,0),1,gg));
