pixel_eqs_eel:
ddx=ddx+q2*.0000001-q5*(pow(rad,q4)*ang*3.14)*.000001;
ddy=ddy+cos(time*q4+q1*frame)*.00000001;
ddx=if(above(ddx,.005),-.005,if(below(ddx,-.005),.005,ddx));
ddy=if(above(ddy,.005),-.005,if(below(ddy,-.005),.005,ddy));

dx=ddx;
