frame_eqs_eel:
warp=warp/bass;
dx=-0.0005;
dy=-0.0005;
x_wave_x = 0.5+0.3*sin(bass+treb+mid);
wave_r = 1 + sin(-x_wave_x*6.28);
wave_g = abs(sin(2*x_wave_x*6.28));
wave_b = sin(x_wave_x*6.28);
treb_effect = if(above(treb_att,1.4),pow(0.99,treb_att),1);
net_effect = if(above(bass_att,0.8*treb_att),1,treb_effect);
zoom = net_effect*bass_att;
rot = rot + rot_residual*.5;
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*0.96+1.3);
shift = (tan(time*7)) -0.05;
shift = if(above(shift,0),0,if(below(shift,-0.1),-0.1,shift));
rot_residual = if(equal(bass_thresh,2),shift,rot_residual);

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1 = texture (sampler_main, uv);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ((max (tmpvar_1.xyz, texture (sampler_main, 
   (uv + (vec2(0.0, 13.0) * ((tmpvar_1.xy - 0.5) * texsize.zw)))
 ).xzy) - 0.008) * 0.96);
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    ret = max(ret,  tex2D(sampler_main, uv + (ret.xy-0.5)*texsize.zw*13*float2(0,1)).xzy );   

    // darken (decay) over time
    ret = (ret - 0.008)*0.96;
}
