warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 uv2 = uv;
    const float mipbias = 0;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, uv2 + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.37) * 0.03;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;    
}

