shapes.frame_eqs_eel:
sounds=(bass+treb+mid)/3;

rad=rad*sounds*0.6;

r=r+sin(bass)*g;
g=g+sin(mid)*g;


x=x+0.207*cos(time*bass*0.1);
y=y+0.207*sin(time*bass*0.1);

g2=g2+0.09*sin((treb+mid)*1.23);
g=g+0.09*cos((bass+mid)*1.37);

sounds=(bass+mid+treb)/3;

rad=rad*(treb+mid)/3;

x=x+0.300*sin(time*bass);
y=y+0.317*cos(time*bass);

sounds=(bass+treb+mid)/3;
att=(bass_att+treb_att+mid_att)/3;
att=(bass_att+treb_att+mid_att)/3;
r2=r2+(0.25*sin(time*6.28))-bass;
g2=g2+(0.25*sin(time*6.28))-bass;

rad=rad;
x=x+0.217*cos(time*2.3)+sounds*0.1;
y=y+0.217*sin((time*3.5)+(sounds*0.3));

frame_eqs_eel:
wave_a = 0;
//decay = 0.1;

vol = (bass_att + treb_att + mid_att)*.333333;

tic = min(time-tin,.1);
tin = time;

ra = 10;
treb_avg = tic*(treb_avg*(1/tic - ra) + ra*treb);
mid_avg = tic*(mid_avg*(1/tic - ra) + ra*mid);
bass_avg = tic*(bass_avg*(1/tic - ra) + ra*bass);
vav = tic*(vav*(1/tic - ra) + ra*(bass+treb+mid)*.33333);

tt = tt + tic*treb_avg;
mt = mt + tic*mid_avg;
bt = bt + tic*bass_avg;

sp = abs(vav - slide)*.1;
slide = if(above(slide,vav),slide-tic*sp,slide+tic*sp) + (1-toc)*vav;
toc = 1;

vt = vt + tic*slide;

q1 = bt;
q2 = mt;
q3 = tt;
q4 = slide*1;
q5 = vt*.05 + 150;

q6 = .01;//tic;

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = (max (max (texture (sampler_main, uv).xyz, texture (sampler_main, 
   ((tmpvar_3 * (1.0 - (8.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz), texture (sampler_main, (
   (tmpvar_3 * (1.0 + (8.0 * sqrt(tmpvar_2))))
  + 0.5)).xyz) - 0.03);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_main, uv).xyz + (clamp (
   ((((texture (sampler_blur2, uv).xyz * scale2) + bias2) * 2.8) - 0.13)
 , 0.0, 1.0) * vec3(1.4, 0.3, 1.3)));
 ret = tmpvar_1.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.03;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret += saturate(GetBlur2(uv)*2.8 - 0.13)*float3(1.4,0.3,1.3);
}
