shader_body
{
  float2 uv2 = uv;

  // diffused version:
  float ss = 0.1;
  float3 blur = 0.25*(
    tex2D(sampler_main, uv2+ss*texsize.zw*float2( 1, 1)).xyz +
    tex2D(sampler_main, uv2+ss*texsize.zw*float2(-1, 1)).xyz +
    tex2D(sampler_main, uv2+ss*texsize.zw*float2( 1,-1)).xyz +
    tex2D(sampler_main, uv2+ss*texsize.zw*float2(-1,-1)).xyz
  );

  // diffusion:  
  ret = blur;

  float F2 = 0.035;
  float k2 = 0.060;
  float s = 0.025;
  float scale = 4;

  // reaction:
  float3 z = ret;
  float uvv = ret.x*ret.y*ret.y;
  z.x += (-uvv + F2*(1-ret.x))*scale;
  z.y += (uvv - (F2+k2)*ret.y)*scale;
  //z.x += s*(16-ret.x*ret.y);
