mtimer=1;
ttimer=1;
bfp=fps/10;
mfp=fps/10;
tfp=fps/10;

frame_eqs_eel:
tempo16=480;//16th note res base

//quick transients
sharpnew= (treb);
heartnew= (mid );
bluntnew= (bass);

volnew=sharpnew+heartnew+bluntnew;

//re= rising edge, b=bass,etc  adjust sens = sensitivity lower for greater sensitivity, higher for greater tolerance of vol change
sens2 = 10;//maybe think about dynamically adjusting this according to the current milkdrop built in annoying volume sensitivity, lower during long durations of loudness where milkdrop is less sensitive, and vice versa
sens  = 1/(bass+treb+mid)*5.5;

bre=if(below(blunt*sens,bluntnew),2,0);//if the previous blunt was much lower than the current, we hit the rising edge, theoretically
mre=if(below(heart*sens,heartnew),2,0);
tre=if(below(sharp*sens,sharpnew),2,0);
bfp=if(below(bfp,0),fps/10,bfp-1);//number of Frames to let Pass after each rising edge is detected, failure to use this will probably result in several coccurrent rising edges, should be a function of fps
mfp=if(below(mfp,0),fps/10,mfp-1);
tfp=if(below(tfp,0),fps/10,tfp-1);
