float sigmoid(float t, float c){
   return 1 / ( 1 + exp(t*c));
}

float2 vortex(float2 domain, float2 position, float2 motionVector, float4 domainAspect, float radius, float sharpness, float spin, float zoom){
   domain -= position;
   domain *= domainAspect.xy;
   float sigmoidFilter = sigmoid( length(domain) - radius , sharpness );
   domain +=  + motionVector*sigmoidFilter;
   spin *= sigmoidFilter;
   float s = sin(spin);
   float c = cos(spin);
   float2 uv_rot;
   uv_rot.x = c*domain.x - s*domain.y;
   uv_rot.y = s*domain.x + c*domain.y;
   uv_rot *= domainAspect.zw;
   uv_rot += position;
   return saturate(position + (uv_rot-position)*lerp(1,zoom,sigmoidFilter));
}

