warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;
    ret += tex2D(sampler_blur2, uv).xyz*0.1;
    ret /= 1.1;

    // darken over time
    ret -= 0.02;//*= 0.95; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz*1 +
              GetBlur1(uv)*3 +
              GetBlur2(uv)*0 +
              GetBlur3(uv)*0 +
              0;
    ret += GetBlur2( (uv-0.5)*0.333 + 0.5);
    ret *= 1;
}
