warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = (max (max (texture (sampler_main, uv).xyz, texture (sampler_main, 
   ((tmpvar_3 * (1.0 - (8.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz), texture (sampler_main, (
   (tmpvar_3 * (1.0 + (8.0 * sqrt(tmpvar_2))))
  + 0.5)).xyz) - 0.034);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec2 tmpvar_1;
 tmpvar_1.x = cos((13.5 * (uv.y / 0.9)));
 tmpvar_1.y = sin((19.5 * (uv.x / 0.9)));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = max (texture (sampler_main, uv).xyz, texture (sampler_fc_main, (uv + (0.045 * tmpvar_1))).zyx);
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.034;
}

