 vec4 tmpvar_5;
 tmpvar_5.w = 0.0;
 tmpvar_5.xyz = max (tmpvar_4, (texture (sampler_main, (uv + tmpvar_2)) * 0.97)).xyz;
 vec4 tmpvar_6;
 tmpvar_6.w = 0.0;
 tmpvar_6.xyz = max (tmpvar_5, (texture (sampler_main, (uv + 
   (tmpvar_2 * 2.0)
 )) * 0.97)).xyz;
 ret_1 = (max (tmpvar_6, (texture (sampler_main, 
   (uv + (tmpvar_2 * 3.0))
 ) * 0.9)).xyz * 0.92);
 vec4 tmpvar_7;
