shapes.frame_eqs_eel:
xp=0.53129;
yp=0.25;
xcorr=yp*0.18+0.3;
ang=time*150;
xq=xp*sin(ang);
yq=yp;
zq=xp*cos(ang);
xp=xq*sin(ang)+zq*cos(ang);
yp=yq;
zp=xq*cos(ang)-zq*sin(ang);
xq=xp;
yq=yp*sin(ang)+zp*cos(ang);
zq=yp*cos(ang)-zp*sin(ang);
zq=zq+3.14;
xs=xq/zq;
ys=yq/zq;
x=min(abs(xs*5-xcorr),1);
y=min(abs(ys*1.3+0.5),1);
r=min(abs(sin(time)),1);
g=1-min(abs(sin((1-r)*3.14)),1);
b=min(abs(sin(1-g)),1);
rad=(bass+mid+treb+2)*0.05;

