warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = ((max (
   max (texture (sampler_main, uv).xyz, texture (sampler_main, ((tmpvar_3 * (1.0 - 
     (8.0 * sqrt(tmpvar_2))
   )) + 0.5)).xyz)
 , texture (sampler_main, 
   ((tmpvar_3 * (1.0 + (8.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz) - 0.03) * 0.98);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2 = ((0.5 - uv) + 0.5);
 ret_1 = (mix (texture (sampler_main, uv).xyz, texture (sampler_main, tmpvar_2).xyz, vec3(0.5, 0.5, 0.5)) * 2.0);
 ret_1 = (((
   ((texture (sampler_blur3, uv).xyz * scale3) + bias3)
  * 2.0) + (
   ((texture (sampler_blur3, tmpvar_2).xyz * scale3) + bias3)
  * 2.0)) + ret_1);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret = (ret-0.03)*0.98;
}

comp_hlsl:
shader_body
{

    float2 uv_echo = (uv - 0.5)*1.000*float2(-1,-1) + 0.5;
    ret = lerp( tex2D(sampler_main, uv).xyz, 
                tex2D(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo
    ret *= 2.00; //gamma
    float3 retish = 1 - ret*(1-ret)*4; //solarize
    ret = GetBlur3(uv)*2 + GetBlur3(uv_echo)*2 + ret;
}
